 <?phpclass myDB { //connects to mysql        private $connection;    /*private $user;    private $pwd;    private $host;    private $db;*/    function __construct($userName, $password, $server, $database) {        /*$this->user=$userName;        $this->pwd=$password;        $this->host=$server;        $this->db=$database;*/        try {            $this->connection=new mysqli($server, $userName, $password, $database);        }        catch(Exception $e){            echo "error: " . $e->getMessage();        }    }    function execute($sql){        $result=mysqli_query($this->connection,  $sql);        if(!$result){            throw new Exception('Query Unsuccessful');        }        elseif(gettype($result)!='boolean') {            $rows = array();            while($r = mysqli_fetch_assoc($result)) {                $rows['object_name'][] = $r;            }            echo json_encode($rows); //return data in json format        }        else {            echo '{"success":true}';        }    }    function trackUse($table, $ip, $page){        $sql="INSERT INTO " . $table . " VALUES ('" . $ip . "', '" . $page . "')";        $result=mysqli_query($this->connection,  $sql);        if(!$result){            echo "error somewhere";        }    }    }?>